import React, { useState, useEffect } from "react";
import "../css/Todos.css";
import axios from 'axios';

const Todos = ({ user }) => {
  const [todos, setTodos] = useState([]);
  const [sorting, setSorting] = useState("sequential");
  const [newTodo, setNewTodo] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:3010/users/${user.id}/todos`)
      .then(response => {
        setTodos(response.data);
        console.log('success');
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleSortingChange = (e) => {
    setSorting(e.target.value);
  };

  var sortedTodos = todos.sort((a, b) => {
    switch (sorting) {
      case "sequential":
        return a.id - b.id;
      case "completed":
        return a.completed - b.completed;
      case "alphabetical":
        return a.title.localeCompare(b.title);
      case "random":
        return Math.random() - 0.5;
      default:
        return 0;
    }
  });

  const handleCheckBoxChange = (todo_id) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === todo_id) {
        return { ...todo, completed: Number(!todo.completed) };
      }
      return todo;
    });
   
    setTodos(updatedTodos);
  };

  const handleAddTodo = () => {
    const newTodoObj = {
      id: todos.length + 1,
      userId: user.id,
      title: newTodo,
      completed: 0,
    };
    const updatedTodos = [...todos, newTodoObj];
   
    setTodos(updatedTodos);
    setNewTodo("");
  };

  const handleUpdateTodo = (todo_id, updatedTitle) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === todo_id) {
        return { ...todo, title: updatedTitle };
      }
      return todo;
    });
   
    setTodos(updatedTodos);
  };

  const handleDeleteTodo = (todo_id) => {
   // const updatedTodos = todos.filter((todo) => todo.id !== todo_id); 
   axios.delete(`http://localhost:3010/todos/${todo_id}`)
   .then(response => {
   //  console.log(response.data);
     setTodos(response.data);
     console.log('success');
   })
   .catch(error => {
     console.error(error);
   });
   axios.get(`http://localhost:3010/users/${user.id}/todos`)
   .then(response => {
     setTodos(response.data);
     console.log('success');
   })
   .catch(error => {
     console.error(error);
   });
  };

  return (
    <div className="todos-container">
      <div className="sorting-container">
        <label htmlFor="sorting">Sort by:</label>
        <select id="sorting" value={sorting} onChange={handleSortingChange}>
          <option value="sequential">Sequential</option>
          <option value="completed">Completed</option>
          <option value="alphabetical">Alphabetical</option>
          <option value="random">Random</option>
        </select>
      </div>
      <hr></hr>
      <div className="todos-list">
        {sortedTodos.map((todo) => (
          <div key={todo.id} className="todo-item">
            <input
              type="checkbox"
              onChange={() => handleCheckBoxChange(todo.id)}
              style={{ accentColor: "#cf3a6c" }}
              checked={todo.completed}
            />
            {todo.completed === 1 ? (
              <span>
                <s>{todo.title}</s>
              </span>
            ) : (
              <span>{todo.title}</span>
            )}
            <button
              onClick={() => {
                const updatedTitle = prompt(
                  "Enter the updated title:",
                  todo.title
                );
                if (updatedTitle) {
                  handleUpdateTodo(todo.id, updatedTitle);
                }
              }}
            >
              Update
            </button>
            <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
          </div>
        ))}
      </div>
      <div className="add-todo-container">
        <input
          type="text"
          placeholder="Enter a new todo"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
        />
        <button onClick={handleAddTodo}>Add Todo</button>
      </div>
    </div>
  );
};

export default Todos;
