import React, { useState, useEffect, useRef } from "react";
import { Link, Navigate, useParams, useNavigate } from "react-router-dom";
import "../css/Posts.css";
import axios from 'axios';

function Posts({ user }) {
  const [posts, setPosts] = useState([]);
  const [selectedPostId, setSelectedPostId] = useState(null);
  const [comments, setComments] = useState([]);
  const [editedPost, setEditedPost] = useState(null);
  const [newPost, setNewPost] = useState({ title: "", body: "" });
  const { postId } = useParams();
  const navigate = useNavigate();
  const postRef = useRef(null);
  const [isAddingPost, setIsAddingPost] = useState(false);

  useEffect(() => {
    if (user && user.id) { // Add a conditional check
      axios.get(`http://localhost:3010/users/${user.id}/todos`)
        .then(response => {
          setPosts(response.data);
          console.log('success');
        })
        .catch(error => {
          console.error(error);
        });
    }
  }, [user]);

  useEffect(() => {
    // Scroll to the edited post when it's opened for editing
    if (editedPost) {
      postRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [editedPost]);

  const newPostDraft = {
    title: newPost.title,
    body: newPost.body
  };

  const handleAddPost = () => {
    setIsAddingPost(true);
  };

  const handleCommentsClick = (postId) => {
    setSelectedPostId(postId);

    fetch(`https://jsonplaceholder.typicode.com/comments?postId=${postId}`)
      .then((response) => response.json())
      .then((data) => {
        setComments(data);
      })
      .catch((error) => {
        console.error("Error fetching comments:", error);
      });
  };

  const handleCloseCommentsClick = () => {
    setSelectedPostId(null);
  };

  const handlePostClick = (postId) => {
    navigate(`/Posts/${postId}`);
  };

  const handleEditPost = (postId) => {
    const selectedPost = posts.find((post) => post.id === postId);
    setEditedPost(selectedPost);
  };

  const handleSavePost = () => {
    if (editedPost) {
      const updatedPosts = posts.map((post) => {
        if (post.id === editedPost.id) {
          return editedPost;
        }
        return post;
      });
      setPosts(updatedPosts);
      setEditedPost(null);
    }
  };

  const handleCancelEdit = () => {
    setEditedPost(null);
  };

  const handleDeletePost = (postId) => {
    const updatedPosts = posts.filter((post) => post.id !== postId);
    setPosts(updatedPosts);
  };

  const handleNewPostChange = (e) => {
    setNewPost({ ...newPost, [e.target.name]: e.target.value });
  };

  const handleSaveNewPost = () => {
    const newPostWithId = {
      ...newPost,
      id: Math.floor(Math.random() * 1000) + 1,
      userId: user.id,
    };
    const updatedPosts = [...posts, newPostWithId];
    setPosts(updatedPosts);
    setNewPost({ title: "", body: "" });
    setIsAddingPost(false);
  };

  const handleCancelAddPost = () => {
    setIsAddingPost(false);
  };

  return (
    <div>
      <div className="posts-container">
        {posts.map((post) => (
          <div
            key={post.id}
            ref={post.id === editedPost?.id ? postRef : null}
            className={postId === post.id ? "selected-post post" : "post"}
          >
            <h3>{post.title}</h3>
            {editedPost && editedPost.id === post.id ? (
              <textarea
                value={editedPost.body}
                onChange={(e) =>
                  setEditedPost({ ...editedPost, body: e.target.value })
                }
              />
            ) : (
              <p>{post.body}</p>
            )}
            {selectedPostId === post.id && (
              <div>
                <h4>Comments:</h4>
                {comments.map((comment) => (
                  <div key={comment.id} className="comment">
                    <p>"Name: " {comment.name}</p>
                    <p>"Email: " {comment.email}</p>
                    <p>{comment.body}</p>
                  </div>
                ))}
              </div>
            )}
            {selectedPostId === post.id ? (
              <button onClick={handleCloseCommentsClick}>
                Close Comments
              </button>
            ) : (
              <button onClick={() => handleCommentsClick(post.id)}>
                View Comments
              </button>
            )}
            {editedPost && editedPost.id === post.id ? (
              <>
                <button onClick={handleSavePost}>Save</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </>
            ) : (
              <button onClick={() => handleEditPost(post.id)}>Edit</button>
            )}
            <button onClick={() => handleDeletePost(post.id)}>Delete</button>
          </div>
        ))}
      </div>
      <div className="add-post-container">
        <button className="add-post-button" onClick={handleAddPost}>
          Add Post
        </button>
      </div>

      {isAddingPost && (
        <div className="post">
          <h3>New Post</h3>
          <input
            type="text"
            name="title"
            placeholder="Post title"
            value={newPostDraft.title}
            onChange={handleNewPostChange}
          />
          <textarea
            name="body"
            placeholder="Post body"
            value={newPostDraft.body}
            onChange={handleNewPostChange}
          />
          <button onClick={handleSaveNewPost}>Save</button>
          <button onClick={handleCancelAddPost}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default Posts;
