import React, { useState, useEffect, useRef } from "react";
import { Link, Navigate, useParams, useNavigate } from "react-router-dom";
import "../css/Posts.css";
import axios from 'axios';

function Posts({ user }) {
  const [posts, setPosts] = useState([]);
  const [selectedPostId, setSelectedPostId] = useState(null);
  const [comments, setComments] = useState([]);
  const [editedPost, setEditedPost] = useState(null);
  const [editedComment, setEditedComment] = useState(null);
  const [newPost, setNewPost] = useState({ title: "", body: "" });
  const [newComment, setNewComment] = useState({ name: "", email: "", body: "" });
  const { postId } = useParams();
  const navigate = useNavigate();
  const postRef = useRef(null);
  const [isAddingPost, setIsAddingPost] = useState(false);
  const [isAddingComment, setIsAddingComment] = useState(false);

  useEffect(() => {
    if (user && user.id) { // Add a conditional check
      axios.get(`http://localhost:3010/users/${user.id}/posts`)
        .then(response => {
          setPosts(response.data);
          console.log('success');
      })
        .catch(error => {
          console.error(error);
      });
    }
  }, [user]);

  useEffect(() => {
    // Scroll to the edited post when it's opened for editing
    if (editedPost) {
      postRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [editedPost]);

  const newPostDraft = {
    title: newPost.title,
    body: newPost.body
  };

  const handleAddPost = () => {
    setIsAddingPost(true);
  };

  const handleCommentsClick = (postId) => {
    console.log(postId);
    setSelectedPostId(postId);
    axios
      .get(`http://localhost:3010/comments/${postId}`)
      .then((response) => {
        // Handle the response data
        const comments = response.data;
        console.log(comments);
        // Perform further operations with the comments
        setComments(comments);
      })
      .catch((error) => {
        // Handle any errors
        console.error('Error retrieving comments:', error);
      });
  };

  const handleCloseCommentsClick = () => {
    setSelectedPostId(null);
  };

  const handlePostClick = (postId) => {
    navigate(`/Posts/${postId}`);
  };

  const handleEditPost = (postId) => {
    const selectedPost = posts.find((post) => post.id === postId);
    setEditedPost(selectedPost);
  };

  const handleSavePost = () => {
    if (editedPost) {
      const updatedPosts = posts.map((post) => {
        if (post.id === editedPost.id) {
          console.log(editedPost);
          axios
            .put(`http://localhost:3010/posts/${editedPost.id}`, editedPost)
            .then((response) => {
              // Handle the response
              console.log('Post updated successfully');
              // Perform further operations if needed
              setPosts(updatedPosts);
              setEditedPost(null);
            })
            .catch((error) => {
              // Handle any errors
              console.error('Error updating post:', error);
            });
          return editedPost;
        }
        return post;
      });

    }
  };

  const handleCancelEdit = () => {
    setEditedPost(null);
  };

  const handleDeletePost = (postId) => {
    axios
      .delete(`http://localhost:3010/posts/${postId}`)
      .then((response) => {
        // Handle the success response
        console.log('Post deleted successfully');
        // Perform further operations if needed
    const updatedPosts = posts.filter((post) => post.id !== postId);
    setPosts(updatedPosts);
      })
      .catch((error) => {
        // Handle any errors
        console.error('Error deleting post:', error);
      });

  };

  const handleNewPostChange = (e) => {
    setNewPost({ ...newPost, [e.target.name]: e.target.value });
  };

  const handleSaveNewPost = () => {
    const newPostWithId = {
      ...newPost,
      user_id: user.id,
    };
    console.log(newPostWithId);

    axios
      .post('http://localhost:3010/posts', newPostWithId)
      .then((response) => {
        // Handle the response data
        const postId = response.data.id;
        console.log('New post created with ID:', postId);
        // Perform further operations with the new post ID
    const updatedPosts = [...posts, newPostWithId];
    setPosts(updatedPosts);
    setNewPost({ title: "", body: "" });
    setIsAddingPost(false);
      })
      .catch((error) => {
        // Handle any errors
        console.error('Error creating post:', error);
      });


  };

  const handleCancelAddPost = () => {
    setIsAddingPost(false);
  };

  const handleAddComment = () => {
    setIsAddingComment(true);
  };

  const handleSaveComment = () => {
    if (editedComment) {
      // Logic to save the edited comment
      setEditedComment(null);
    } else {
      // Logic to save the new comment
      const newCommentWithId = {
        ...newComment,
        id: Math.floor(Math.random() * 1000) + 1,
        postId: selectedPostId,
      };

      axios
      .post("http://localhost:3010/comments", newComment)
      .then(response => {
        console.log("Comment added successfully");
        const updatedComments = [...comments, newCommentWithId];
        setComments(updatedComments);
        setNewComment({ name: "", email: "", body: "" });
        setIsAddingComment(false);
      })
      .catch(error => {
        console.error("Error adding comment:", error);
        // Handle error
      });
     
    }
  };

  const handleCancelEditComment = () => {
    setEditedComment(null);
  };

  const handleDeleteComment = (commentId) => {
    const updatedComments = comments.filter((comment) => comment.id !== commentId);
    setComments(updatedComments);
  };

  const handleNewCommentChange = (e) => {
    setNewComment({ ...newComment, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <div className="posts-container">
        {posts.map((post) => (
          <div
            key={post.id}
            ref={post.id === editedPost?.id ? postRef : null}
            className={postId === post.id ? "selected-post post" : "post"}
          >
            <h3>{post.title}</h3>
            {editedPost && editedPost.id === post.id ? (
              <textarea
                value={editedPost.body}
                onChange={(e) =>
                  setEditedPost({ ...editedPost, body: e.target.value })
                }
              />
            ) : (
              <p>{post.body}</p>
            )}
            {selectedPostId === post.id && (
              <div>
                <h4>Comments:</h4>
                {comments.map((comment) => (
                  <div key={comment.id} className="comment">
                    {editedComment && editedComment.id === comment.id ? (
                      <div>
                        <input
                          type="text"
                          name="name"
                          placeholder="Name"
                          value={editedComment.name}
                          onChange={(e) =>
                            setEditedComment({
                              ...editedComment,
                              name: e.target.value,
                            })
                          }
                        />
                        <input
                          type="email"
                          name="email"
                          placeholder="Email"
                          value={editedComment.email}
                          onChange={(e) =>
                            setEditedComment({
                              ...editedComment,
                              email: e.target.value,
                            })
                          }
                        />
                        <textarea
                          name="body"
                          placeholder="Comment body"
                          value={editedComment.body}
                          onChange={(e) =>
                            setEditedComment({
                              ...editedComment,
                              body: e.target.value,
                            })
                          }
                        />
                        <button onClick={handleSaveComment}>Save</button>
                        <button onClick={handleCancelEditComment}>
                          Cancel
                        </button>
                      </div>
                    ) : (
                      <>
                        <p>Name: {comment.name}</p>
                        <p>Email: {comment.email}</p>
                        <p>{comment.body}</p>
                        <button onClick={() => setEditedComment(comment)}>
                          Edit
                        </button>
                        <button
                          onClick={() => handleDeleteComment(comment.id)}
                        >
                          Delete
                        </button>
                      </>
                    )}
                  </div>
                ))}
                {isAddingComment ? (
                  <div>
                    <input
                      type="text"
                      name="name"
                      placeholder="Name"
                      value={newComment.name}
                      onChange={handleNewCommentChange}
                    />
                    <input
                      type="email"
                      name="email"
                      placeholder="Email"
                      value={newComment.email}
                      onChange={handleNewCommentChange}
                    />
                    <textarea
                      name="body"
                      placeholder="Comment body"
                      value={newComment.body}
                      onChange={handleNewCommentChange}
                    />
                    <button onClick={handleSaveComment}>Save</button>
                    <button onClick={() => setIsAddingComment(false)}>
                      Cancel
                    </button>
                  </div>
                ) : (
                  <button onClick={handleAddComment}>Add Comment</button>
                )}
              </div>
            )}
            {selectedPostId === post.id ? (
              <button onClick={handleCloseCommentsClick}>Close Comments</button>
            ) : (
              <button onClick={() => handleCommentsClick(post.id)}>
                View Comments
              </button>
            )}
            {editedPost && editedPost.id === post.id ? (
              <>
                <button onClick={handleSavePost}>Save</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </>
            ) : (
              <button onClick={() => handleEditPost(post.id)}>Edit</button>
            )}
            <button onClick={() => handleDeletePost(post.id)}>Delete</button>
          </div>
        ))}
      </div>
      <div className="add-post-container">
        <button className="add-post-button" onClick={handleAddPost}>
          Add Post
        </button>
      </div>

      {isAddingPost && (
        <div className="post">
          <h3>New Post</h3>
          <input
            type="text"
            name="title"
            placeholder="Post title"
            value={newPostDraft.title}
            onChange={handleNewPostChange}
          />
          <textarea
            name="body"
            placeholder="Post body"
            value={newPostDraft.body}
            onChange={handleNewPostChange}
          />
          <button onClick={handleSaveNewPost}>Save</button>
          <button onClick={handleCancelAddPost}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default Posts;
